using System;using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.EventSystems;namespace Assets.Scripts.UI{    public class UICanvasScript : MonoBehaviour, IPointerClickHandler    {        public GameObject elementPrefab;        public void OnPointerClick(PointerEventData eventData)        {
            GameObject newElem = GameObject.Instantiate(elementPrefab, new Vector3(eventData.position.x, eventData.position.y), Quaternion.identity);            newElem.transform.SetParent(this.gameObject.transform);        }        void Awake()        {        }        // Use this for initialization        void Start()        {
        }        // Update is called once per frame        void Update()        {        }    }}